__ht__function(data, view) {
var closeColor = data.getAttr('closeSwitchColor')
var openColor = data.getAttr('openSwitchColor')
if (data.getAttr('switchState') === 0) {
    return closeColor || '#cdcdcd'
} else if (data.getAttr('switchState') === 1) {
    return openColor || data.getAttr('background')
}
}

