background.func:
__ht__function(data, view) {
if (data.getAttr('alarmState') === 1) {
    return '#32BFD0'
  } else if (data.getAttr('alarmState') === 0) {
    return '#D04848'
  } else if (data.getAttr('alarmState') === 2) {
    return '#A5A5A5'
  }
}

